# load in packages
library(tidyverse)
library(rvest)
library(purrr)
# create the function
web_func &lt;- function(n) {
  title &lt;- gsub(" ", "-", n)
  title &lt;- gsub("and", "-", title)
  title &lt;- gsub("@", "at", title)
  title &lt;- gsub("&", "-", title)
  title &lt;- gsub("-+", "-", title)
  html &lt;- paste0("https://www.pomona.edu/", title)
  page &lt;- read_html(html)
quantity &lt;- page |&gt;
  html_elements(".text-7xl") |&gt;
  html_text()
facts &lt;- page |&gt;
  html_elements(".fact .text-xl") |&gt;
  html_text()
# account for the sites that have no facts
if (length(facts) == 0)
  facts &lt;- "N/A"
if (length(quantity) == 0)
  quantity &lt;- "N/A"
# return a tibble
data &lt;- tibble(
  topic = n,
  quantity = quantity,
  facts = facts
)
return(data)
}

